import React from "react";


const WordDisplay = ({ displayWord }) => {



    return (
        <div className="word-display">

            <>
                {/* Word */}
                <h2>{displayWord[0].word}</h2>

                {/* Definition 1 */}
                <h3>Definitions</h3>

                <p><b>{displayWord[0].meanings[0].partOfSpeech}</b> : {capitialize(displayWord[0].meanings[0].definitions[0].definition)} </p>


            </>



        </div>
    )
}

export default WordDisplay;
